const layoutProps = {

};
class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components}>{`Introduction`}</MDXTag>
<MDXTag name="p" components={components}>{`Do you prefer the usage of "ES6 Promise"? If you do, you will like the usage of "Fetch" too.`}<MDXTag name="br" components={components} parentName="p"></MDXTag>{`
`}{`Compared to "Ajax", "Fetch" owns a competitive feature: promise, which synchronize asynchronous methods elegantly, the meaning and the usage of "Fetch"  can be understood easily as well.`}<MDXTag name="br" components={components} parentName="p"></MDXTag>{`
`}{`Here, I'd like to list the most common usage of "Fetch". `}</MDXTag>
<MDXTag name="h1" components={components}>{`Flow`}</MDXTag>
<MDXTag name="p" components={components}>{`The flow of fetching stuff is simple:`}<MDXTag name="br" components={components} parentName="p"></MDXTag>{`
`}<MDXTag name="img" components={components} parentName="p" props={{"src":"https://sfault-image.b0.upaiyun.com/381/502/3815021434-595f6cfd6f71b_articlex","alt":null}}></MDXTag>{`   `}</MDXTag>
<MDXTag name="h1" components={components}>{`Usage`}</MDXTag>
<MDXTag name="h2" components={components}>{`Fetch once`}</MDXTag>
<MDXTag name="p" components={components}>{`Suppose we would fetch the content of an remote html`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-js"}}>{`fetch('./data/test.html')
    .then(function (response) {
        return response.text()    // return a promise 
    })
    .then(function (body) {
        console.log( body )    // log: html content
    })
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components}>{`Fetch data right after the other data fetched(Chain)`}</MDXTag>
<MDXTag name="p" components={components}>{`If we'd like to fetch data(json) right after fetching content(html)`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-js"}}>{`fetch('./data/test.html')
    .then(response => {
        return response.text()
    })
    .then(body => {
        console.log(body)
        return fetch('./data/test.json')  // return a promise(\`fetch('/url')\` will return a promise ) 
    })
    .then(response => {
        return response.json()  // return a promise too
    })
    .then(json => {
        console.log(json)  // log: json's data
    })
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components}>{`Complete all fetching action`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-js"}}>{`Promise.all([
    Promise.resolve(fetch('./data/test.html')),
    Promise.resolve(fetch('./data/test.json'))
]).then(data => {
    console.log('Two requests are both completed!')
})
`}</MDXTag></MDXTag>
<MDXTag name="h1" components={components}>{`API`}</MDXTag>
<MDXTag name="p" components={components}><MDXTag name="a" components={components} parentName="p" props={{"href":"https://github.github.io/fetch/"}}>{`Github Fetch Document`}</MDXTag><MDXTag name="br" components={components} parentName="p"></MDXTag>{`
`}<MDXTag name="a" components={components} parentName="p" props={{"href":"https://fetch.spec.whatwg.org/"}}>{`Offcial Manual`}</MDXTag></MDXTag>
<MDXTag name="h1" components={components}>{`Conclusion`}</MDXTag>
<MDXTag name="p" components={components}>{`Fetch, well done!`}</MDXTag>
           </MDXTag>
  }
}
MDXContent.isMDXComponent = true
render( <MDXContent /> )
