<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-js"}}>{`// Create an observer to detect the opening state of light
const basicState = {
    open: false
}
const lightState = new Proxy(basicState, {
    set(obj, prop, value) {
        if (prop === 'open') {
            switch(value) {
                case true: 
                    console.log('Light on!')
                    break
                case false: 
                    console.log('Light off!')
            }
        }

        return true
    }
})


// Turn on light
lightState.open = true    // output: Light on!

// Turn off light
lightState.open = false    // output: Light off!
`}</MDXTag></MDXTag>
